import React,{useState, useEffect} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { fade, makeStyles } from '@material-ui/core/styles';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import SearchIcon from '@material-ui/icons/Search';
import Paper from '@material-ui/core/Paper';
import Divider from '@material-ui/core/Divider';
import LocalLibraryIcon from '@material-ui/icons/LocalLibrary';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';


import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import load from "../images/loader.gif";

import * as book from "../utils/book";


const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
      display: 'none',
      [theme.breakpoints.up('sm')]: {
        display: 'block',
      },
    },
    search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.white, 0.25),
      },
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(1),
        width: 'auto',
      },
    },
    searchIcon: {
      padding: theme.spacing(0, 2),
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputRoot: {
      color: 'inherit',
    },
    inputInput: {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        width: '12ch',
        '&:focus': {
          width: '20ch',
        },
      },
    },
    paperSearch:{
      padding: '2px 4px',
      display: 'flex',
      marginTop:'20px',
      alignItems: 'center',
      width: '100%',
    },input: {
      marginLeft: theme.spacing(1),
      flex: 1,
    },
    iconButton: {
      padding: 10,
    },
    divider: {
      height: 28,
      margin: 4,
    },
    card: {
      display: 'flex',
    }, details: {
      display: 'flex',
      flexDirection: 'column',
    },
    content: {
      flex: '1 0 auto',
    },


    cover: {
      width: 151,
    },
    controls: {
      display: 'flex',
      alignItems: 'center',
      paddingLeft: theme.spacing(1),
      paddingBottom: theme.spacing(1),
    },
    playIcon: {
      height: 38,
      width: 38,
    },
    item:{
      float:"left",
    },
    dividerH: {
      height: 1,
      margin: 4,
    },
  }));


function HomeBook(){
    const classes = useStyles();
    const [books,setBooks]=useState([]);

    /// get initial or defaulty book 
const [isLoading,setIsLoading]=useState(false);
    useEffect(()=>{
      setIsLoading(true);
      book.initialBookList().then(response=>{


        console.log(response['items'][0]['volumeInfo']);


        // set result from api to our book state 
        setIsLoading(false);
        setBooks(response['items']);

      });
    },[]);

    return(
<div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
         <MenuBookIcon /> 
          <Typography className={classes.title} variant="h6" noWrap>
            Book Library
          </Typography>
          <div className={classes.search}>
            <div className={classes.searchIcon}>
              <SearchIcon />
            </div>
            <InputBase
              placeholder="Searchâ€¦"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
              inputProps={{ 'aria-label': 'search' }}
            />
          </div>
        </Toolbar>
      </AppBar>
      <div className={classes.root}>

        {/* search input field */}

     <center>
        <div className="col-md-6">
        <Paper component="form" className={classes.paperSearch}>
      <IconButton className={classes.iconButton} aria-label="menu">
        <LocalLibraryIcon/>
      </IconButton>
      <InputBase
        className={classes.input}
        placeholder="Search a book"
      />
      <IconButton type="submit" className={classes.iconButton} aria-label="search">
        <SearchIcon />
      </IconButton>
    </Paper>
        </div>

{/* end of search input */}

<br/>

{/* defaulty list of books */}

     <div className="col-md-6">
{isLoading?<center><img src={load} width="80px" height="80px"/></center>:null}
{
books.map((d)=>{
return (
<div>

  <table width="100%">
    <tr>
      <td rowspan="3">
      <img  src={d['volumeInfo']['imageLinks']['smallThumbnail']} width="50" heigh="120px" />
      </td>
      <td>
      <font color="grey" style={{fontSize:"14px"}}>{d['volumeInfo']['title']}</font>
      </td>
    </tr>
    <tr><td><font style={{fontSize:"12px",float:"left"}}>{d['volumeInfo']['industryIdentifiers'][0]['identifier']}</font></td></tr>
    <tr><td><font style={{fontSize:"12px"}}>{d['volumeInfo']['authors'].map((a)=>{ return (<font>{a}</font>)})}</font></td></tr>
  </table>


  {/* <Grid container spacing={0}>
        <Grid item xs={12}>
          <Grid container spacing={0}>
          <Grid item xs={3}>
            <img  src={d['volumeInfo']['imageLinks']['smallThumbnail']} />
            </Grid>
            <Grid item xs={9}>
            <Grid container spacing={0}>
            <Grid className={classes.item} item xs={12}>
            <div style={{float:"left"}}>
              <p style={{float:"left"}}><font color="black" style={{fontSize:"15px"}}>{d['volumeInfo']['title']}</font></p>
<br/>
              <p style={{float:"left"}}><font color="grey" style={{fontSize:"12px"}}>{d['volumeInfo']['subtitle']}</font></p>

              </div> 

            </Grid>

              </Grid>

            </Grid>
          </Grid>
        </Grid>
      </Grid> */}
      <Divider className={classes.dividerH} orientation="horizontal" />

</div>);
})
}

    </div>   



      </center>



      </div>
      </div>

    );
}
export default HomeBook
