import React,{Component} from "react"
import Appbar from 'muicss/lib/react/appbar';
import logo from '../images/logo.png';

import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';

import Paper from '@material-ui/core/Paper';

import logo_white from "../images/logo_white.png";

import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import load from "../images/loader.gif";
import Cookies from 'js-cookie';
import { Redirect } from 'react-router';

class HomeAppBar extends Component{


  constructor(){
    super()
    this.state={
      signstatus:false,
      personalInfo:false,
      activeStep:0,
      signupStatus:false,
      email:null,
      password:null,
      signinLoad:false,
      signinFound:false,
      rfirstname:null,
      rlastname:null,
      remail:null,
      rpassword:null

    }
  }


  changeSignin(){
    let status=this.state.signstatus;
    if(status===true){
      this.setState({signstatus:false});
    } else{
      this.setState({signstatus:true});
    }  
  }

  closeLogin(){
    this.setState({signstatus:false});
  }



  handleNext(){
    let temp=this.state.activeStep + 1;
    this.setState({activeStep:temp});
    // setActiveStep((prevActiveStep) => prevActiveStep + 1);
  }

  handleBack(){
    let temp=this.state.activeStep - 1;
    this.setState({activeStep:temp})
    // setActiveStep((prevActiveStep) => prevActiveStep - 1);
  }

  handleReset(){
    this.setState({activeStep:0})
    // setActiveStep(0);
  }


  signIn(){
    this.setState({signinLoad:true});
    let email=this.state.email;
    let password=this.state.password;
    if(email!=null && password!=null){
      fetch('/greata/login.php',{
        method:'POST',
        headers:{'content-type': 'application/json;charset=UTF-8',},
        body:JSON.stringify({
          "email":email,
          "password":password
        }),
      }) .then(response => response.json()).then(
        (json)=>{
          let found=json[0]['found'];
          let message=json[0]['message'];
          let user_id=json[0]['user_id'];
          let first_name=json[0]['first_name'];
          let last_name=json[0]['last_name'];
          let email1=json[0]['email'];
          if(found){
            this.setState({signinFound:true});
            this.setState({signinLoad:false});
            console.log("user found");
            Cookies.set('user_id', user_id);
            Cookies.set('first_name', first_name);
            Cookies.set('last_name', last_name);
            Cookies.set('email', email1);
          }else{
            console.log("user not found");
            this.setState({signinLoad:false});
          };
        }
      );

    }else{

      this.setState({signinLoad:false});
      console.log("empty field detected");
      // show error message

    }
  }



  /// register


  openRegister(){
    this.setState({signupStatus:true});
  }

  next(){
    this.setState({signupStatus:false});
   this.setState({personalInfo:true}) 
  }




    render(){

        if (this.state.signinFound=== true) {
            return <Redirect to='/admin/home/' />
          }

    let appbar={backgroundColor: '#1776B0'};

    function rand() {
      return Math.round(Math.random() * 20) - 10;
    }

  function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    // console.log("left:"+left);
    // console.log("top:"+top);

    return {
      top: `42%`,
      left: `47%`,
      transform: `translate(-42%, -47%)`,
      position: 'absolute',
      width: 400,
      border: '1px solid #000',
      backgroundColor:"#F3F3F3",
      padding:20,
    };
  }


  function getModalStyle1() {
    const top = 50 + rand();
    const left = 50 + rand();

    // console.log("left:"+left);
    // console.log("top:"+top);

    return {
      top: `42%`,
      left: `47%`,
      transform: `translate(-42%, -47%)`,
      position: 'absolute',
      width: 600,
      border: '1px solid #000',
      backgroundColor:"#F3F3F3",
      padding:20,
    };
  }



function getSteps() {
  return ['Academic education', 'Vocational training', 'Self training'];
}

function getStepContent(stepIndex) {
  switch (stepIndex) {
    case 0:
      return educationInfo();
    case 1:
      return vacationalTraining();
    case 2:
      return selfTraining();
    default:
      return 'Unknown stepIndex';
  }
}

const steps = getSteps();

function selfTraining(){
  return(


    <div class="container">

    <div className="row">
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >Field</font>
      </div>
    </div>



    <div className="row">
      <div className="col-md-12">
      <input type="text" style={{height:"28px"}} className="form-control" placeholder="Field name"/>
      </div>
    </div>

    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >If you have more certificates click  <button type="button" class="btn btn-light" style={{backgroundColor:"#ffffff",height:"25px",fontSize:"10px",textAlign:"center"}}>To add</button></font>
      </div>
    </div>


    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
        <select className="form-control" style={{height:"28px"}} >
          <option>Professional area</option>
        </select>
      </div>
    </div>

    </div>

  );
}

function vacationalTraining(){
  return(


    <div class="container">

    <div className="row">
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >Certificate</font>
      </div>
    </div>



    <div className="row">
      <div className="col-md-12">
      <input type="text" style={{height:"28px"}} className="form-control" placeholder="Training name"/>
      </div>
    </div>

    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
      <input type="text" style={{height:"28px"}} className="form-control" placeholder="School"/>
      </div>
    </div>

    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >If you have more certificates click  <button type="button" class="btn btn-light" style={{backgroundColor:"#ffffff",height:"25px",fontSize:"10px",textAlign:"center"}}>To add</button></font>
      </div>
    </div>



    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
        <select className="form-control" style={{height:"28px"}} >
          <option>Professional area</option>
        </select>
      </div>
    </div>

    </div>



  );
}

function educationInfo(){
  return(
    <div class="container">

    <div className="row">
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >Secondary Education</font>
      </div>
    </div>



    <div className="row">
      <div className="col-md-12">
      <input type="text" style={{height:"28px"}} className="form-control" placeholder="School"/>
      </div>
    </div>

    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
      <input type="text" style={{height:"28px"}} className="form-control" placeholder="Option"/>
      </div>
    </div>

    <div className="row">
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >High Education</font>
      </div>
    </div>


    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
      <input type="text" style={{height:"28px"}} className="form-control" placeholder="Univerty"/>
      </div>
    </div>



    <div className="row">
      <div className="col-md-12">
      <font style={{color:"#848586",fontSize:"12px"}} >Awarded degree</font>
      </div>
    </div>


    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
        <select className="form-control" style={{height:"28px"}} >
          <option>PHD</option>
          <option>Master's degree</option>
          <option>Bachelor's degree</option>
          <option>A2 diploma</option>
        </select>
      </div>
    </div>

    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
        <select className="form-control" style={{height:"28px"}} >
          <option>Education area</option>
        </select>
      </div>
    </div>

    <div className="row" style={{marginTop:"5px"}}>
      <div className="col-md-12">
        <select className="form-control" style={{height:"28px"}} >
          <option>Professional area</option>
        </select>
      </div>
    </div>

  </div>


  );
}


        return (
            <Appbar style={appbar} >

<div className="container"  >
<div className="row" >
    <div className="col-md-2" >

    </div>
    <div className="col-md-8">
    <div className='container'>
                     <div className='row'>
                         <div className='col-md-6' style={{marginTop:"10px"}}>
                         <div className="text-left">
                         <img src={logo} width='100px'/>
                         </div>
                         </div>

                         <div className="col-md-6" style={{marginTop:"10px"}}>
                           <div className="text-right">
                           <button type="button" style={{color:'#1776B0'}} onClick={()=>{ this.changeSignin()}} className="btn btn-light btn-sm">Sign in</button><button type="button" onClick={()=>{this.openRegister()}} style={{marginLeft:"10px"}} className="btn btn-outline-primary btn-sm border-light text-light">Register</button>
                             </div>
                         </div>
                     </div>
                </div>
    </div>
    <div className="col-md-2">

    </div>
</div>
