import React,{Component} from "react"
import Appbar from 'muicss/lib/react/appbar';
import logo from '../images/logo.png';

import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';

import Paper from '@material-ui/core/Paper';

import logo_white from "../images/logo_white.png";

import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import load from "../images/loader.gif";
import Cookies from 'js-cookie';
import { Redirect } from 'react-router';

class HomeAppBar extends Component{


  constructor(){
    super()
    this.state={
      signstatus:false,
      personalInfo:false,
      activeStep:0,
      signupStatus:false,
      email:null,
      password:null,
      signinLoad:false,
      signinFound:false,
      rfirstname:null,
      rlastname:null,
      remail:null,
      rpassword:null,
      rAschool:null,
      rAoption:null,
      rAuniversity:null,
      rAdegree:null,
      rAeducationarea:null,
      rAprofessionalarea:null,
      rVtrainingname:null,
      rVschool:null,
      rVtrainingnameA:[],
      rVschoolA:[],
      rVprofessionalarea:null,
      rSfieldname:null,
      rSfieldnameA:[],
      rSprofessionalarea:null,
      registermessage:''

    }
  }


  changeSignin(){
    let status=this.state.signstatus;
    if(status===true){
      this.setState({signstatus:false});
    } else{
      this.setState({signstatus:true});
    }  
  }

  closeLogin(){
    this.setState({signstatus:false});
  }



  handleNext(){
    let temp=this.state.activeStep + 1;
    this.setState({activeStep:temp});


    // setActiveStep((prevActiveStep) => prevActiveStep + 1);
  }

  handleBack(){
    let temp=this.state.activeStep - 1;
    this.setState({activeStep:temp})
    // setActiveStep((prevActiveStep) => prevActiveStep - 1);
  }

  handleReset(){
    // this.setState({activeStep:0})
    this.register();
  }


  signIn(){
    this.setState({signinLoad:true});
    let email=this.state.email;
    let password=this.state.password;
    if(email!=null && password!=null){
      fetch('/greata/login.php',{
        method:'POST',
        headers:{'content-type': 'application/json;charset=UTF-8',},
        body:JSON.stringify({
          "email":email,
          "password":password
        }),
      }) .then(response => response.json()).then(
        (json)=>{
