import React,{Component} from "react";
import UserAppBar from "./UserAppBar";
import companylogo from "../images/companylogo.png";
import Footer from "./Footer";

class UserFindJob extends Component{

    constructor(){
        super();
        this.state={
            jobs:[],
            isLoading:false,
            query:null,
            country:null
        }
    }

    componentDidMount() {
        this.setState({isLoading:true});
        this.setState({isLoading: true,searching:false});
        fetch('/greata/job.php',{
          method:'GET',
          headers:{'content-type': 'application/json;charset=UTF-8'},
        }
         )
          .then(response => response.json())
          .then(data => this.setState({jobs: data,isLoading: false}));
      }

      search(){
        fetch('/greata/find_job.php?query='+this.state.query,{
            method:'GET',
            headers:{'content-type': 'application/json;charset=UTF-8'},
          }
           )
            .then(response => response.json())
            .then(data => this.setState({jobs: data,isLoading: false}));
      }

    render(){
        const {jobs}=this.state;
        return(
            <div style={{backgroundColor:"#F0F0F0",height:"100%"}}>
                <UserAppBar/>

<div className="container">
                <div className="row" >
    <div className="col-md-1" >

    </div>
    <div className="col-md-10" style={{marginTop:"30px"}}>

{/* form start */}

        <form>
            <div className="row">
                <div className="col-md-4">
                <input onChange={(e)=>{this.setState({query:e.target.value})}} className="form-control" style={{height:"30px"}}  placeholder="What" />
                <font color="#A2A2A2" style={{fontSize:"12px",marginTop:"-20px"}}>Key word Job title,Company name</font>
                </div>
                <div className="col-md-4">
                <input onChange={(e)=>{this.setState({country:e.target.value})}} className="form-control" style={{height:"30px"}}  placeholder="Where" /> 
                <font color="#A2A2A2" style={{fontSize:"12px",marginTop:"0px"}}>Country</font>
                </div>
                <div className="col-md-4">
                    <button onClick={()=>{this.search()}} type="button" className="btn btn-primary" style={{height:"30px",backgroundColor:"#1776B0",textAlign:"center"}} >Find a Job</button>
                </div>
            </div>

        </form>


{/* form end  */}




{/* search result  */}

<div className="row" >
    <div className="col-md-10 border" style={{backgroundColor:"#ffffff"}}>


{/* job item  */}


{jobs.map((job)=>{
    return(<div className="row" style={{marginTop:"10px"}}>
    <div className="col-md-1">
<img src={companylogo} width="50px" height="50px"/>
    </div>
    <div className="col-md-11" style={{fontSize:"12px"}}>
<font color="#2077AF"><b>{job['job_title']}</b></font><br/>
<font color="#000000">{job['company']} | {job['publication_date']} | {job['deadline']}</font><br/>
<font color="#989898">Expirience : {job['experience']}</font>
    </div>
</div>)
})}











{/* end of job item  */}

    </div>
    <div className="col-md-2" >
        advert
    </div>

</div>

{/* end of search result  */}


        </div>
        <div className="col-md-1">

        </div>
        </div>
        </div>

{/* footer  */}

<Footer/>

{/* end of footer */}


        </div>
        )
    }

}

export default UserFindJob;
